import React, { Component } from 'react';
var map;

class Map extends Component {

  componentDidMount() {
    map = new window.google.maps.Map(document.querySelector('#map'), {
      zoom: 14,
      center: {lat: 40.758745, lng: -73.952642 }
    });

    map.data.loadGeoJson('https://pbortnick.github.io/data/nyc.json');
    map.data.setStyle({
      fillColor: 'green',
      strokeWeight: 1,
      clickable: true
    });

    map.data.addListener('click', function(event) {
      document.querySelector('.selection-name').textContent =
      event.feature.getProperty('Name');
    });

    map.data.addListener('click', function(event) {
      nowEditingShape = new google.maps.Polygon({
        paths: shape,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        editable: true
      });
      nowEditingShape.setMap(map);
      event.setEditable(true);
    });
  }
  render() {
    return (
      <div className="map-wrapper">
        <div className="selection-name">Pam</div>
        <br></br>
        <div id="map">
        </div>
      </div>
    );
  }
}

export default Map;
